namespace SolidInvoice\CoreBundle\Twig\Extension;

use Symfony\Component\HttpFoundation\Request;
use Carbon\Carbon;
use DateTime;
use SolidInvoice\CoreBundle\Pdf\Generator;
use SolidInvoice\CoreBundle\SolidInvoiceCoreBundle;
use SolidInvoice\MoneyBundle\Calculator;
use SolidInvoice\SettingsBundle\Exception\InvalidSettingException;
use SolidInvoice\SettingsBundle\SystemConfig;
use Symfony\Component\DependencyInjection\Exception\InvalidArgumentException;
use Symfony\Component\DependencyInjection\Exception\ServiceCircularReferenceException;
use Symfony\Component\DependencyInjection\Exception\ServiceNotFoundException;
use Symfony\Component\HttpFoundation\RequestStack;
use Twig\Environment;
use Twig\Error\LoaderError;
use Twig\Error\SyntaxError;
use Twig\Extension\AbstractExtension;
use Twig\Extension\GlobalsInterface;
use Twig\TwigFilter;
use Twig\TwigFunction;

class GlobalExtension extends AbstractExtension implements GlobalsInterface
{
    private const DEFAULT_LOGO = 'png|';

    /**
     * @var Calculator
     */
    private $calculator;

    /**
     * @var Generator
     */
    private $pdfGenerator;

    /**
     * @var SystemConfig
     */
    private $systemConfig;

    /**
     * @var string|null
     */
    private $installed;

    /**
     * @var RequestStack
     */
    private $requestStack;

    public function __construct(Calculator $calculator, Generator $pdfGenerator, SystemConfig $systemConfig, RequestStack $requestStack, ?string $installed)
    {
        $this->calculator = $calculator;
        $this->pdfGenerator = $pdfGenerator;
        $this->systemConfig = $systemConfig;
        $this->installed = $installed;
        $this->requestStack = $requestStack;
    }

    /**
     * Get global twig variables.
     *
     * @throws InvalidSettingException|InvalidArgumentException|ServiceCircularReferenceException|ServiceNotFoundException
     */
    public function getGlobals(): array
    {
        $globals = [
            'query' => $this->getQuery(),
            'app_version' => SolidInvoiceCoreBundle::VERSION,
            'app_name' => SolidInvoiceCoreBundle::APP_NAME,
        ];

        if ($this->installed) {
            $globals['app_name'] = $this->systemConfig->get('system/company/company_name');
        }

        return $globals;
    }

    /**
     * Get the url query.
     *
     * @throws ServiceCircularReferenceException|ServiceNotFoundException
     */
    protected function getQuery(): array
    {
        $request = $this->requestStack->getCurrentRequest();

        if (!$request instanceof Request) {
            return [];
        }

        $params = array_merge($request->query->all(), $request->attributes->all());

        foreach (array_keys($params) as $key) {
            if (0 === strpos($key, '_')) {
                unset($params[$key]);
            }
        }

        return $params;
    }

    /**
     * {@inheritdoc}
     */
    public function getFilters()
    {
        return [
            new TwigFilter('percentage', function ($amount, float $percentage = 0.0): float {
                return $this->calculator->calculatePercentage($amount, $percentage);
            }),

            new TwigFilter('diff', function (DateTime $date): string {
                return $this->dateDiff($date);
            }),

            new TwigFilter('md5', 'md5'),
        ];
    }

    /**
     * {@inheritdoc}
     */
    public function getFunctions()
    {
        return [
            new TwigFunction('icon', function (string $iconName, array $options = []): string {
                return $this->displayIcon($iconName, $options);
            }, ['is_safe' => ['html']]),

            new TwigFunction('app_logo', function (Environment $env, string $width = 'auto'): string {
                return $this->displayAppLogo($env, $width);
            }, ['is_safe' => ['html'], 'needs_environment' => true]),

            new TwigFunction('can_print_pdf', function (): bool {
                return $this->pdfGenerator->canPrintPdf();
            }),
        ];
    }

    /**
     * @throws InvalidArgumentException|ServiceCircularReferenceException|ServiceNotFoundException|LoaderError|SyntaxError
     */
    public function displayAppLogo(Environment $env, string $width = 'auto'): string
    {
        $logo = self::DEFAULT_LOGO;

        if ($this->installed) {
            try {
                $logo = $this->systemConfig->get('system/company/logo');

                if (null === $logo) {
                    $logo = self::DEFAULT_LOGO;
                }
            } catch (InvalidSettingException $e) {
                $logo = self::DEFAULT_LOGO;
            }
        }

        [$type, $logo] = explode('|', $logo);

        return $env->createTemplate('<img src="data:image/{{ type }};base64,{{ logo }}" class="brand-image" width="'.$width.'"/>')->render(['type' => $type, 'logo' => $logo]);
    }

    /**
     * Displays an icon.
     */
    public function displayIcon(string $iconName, array $options = []): string
    {
        $options = implode(' ', $options);
        $class = sprintf('fa fa-%s', $iconName);

        if (!empty($options)) {
            $class .= ' '.$options;
        }

        return sprintf('<i class="%s"></i>', $class);
    }

    /**
     * Returns a human-readible diff for dates.
     */
    public function dateDiff(DateTime $date): string
    {
        $carbon = Carbon::instance($date);

        return $carbon->diffForHumans();
    }
}
