namespace SolidInvoice\NotificationBundle\Configurator;

use SolidInvoice\NotificationBundle\Form\Type\Transport\TelegramType;
use Symfony\Component\Notifier\Transport\Dsn;
use function sprintf;

/**
 * @codeCoverageIgnore
 */
final class TelegramConfigurator implements ConfiguratorInterface
{
    public static function getName(): string
    {
        return 'Telegram';
    }

    public static function getType(): string
    {
        return 'chatter';
    }

    public function getForm(): string
    {
        return TelegramType::class;
    }

    /**
     * @param array{ token: string, chat_id: string } $config
     */
    public function configure(array $config): Dsn
    {
        return new Dsn(sprintf('telegram://%s@default?channel=%s', $config['token'], $config['chat_id']));
    }
}
